import './App.css';

//NOTE importing nested components
import WeatherScreen from './WeatherScreen'

//NOTE: this is the app component.
//NOTE: a function can be a component.
//NOTE: a class can also be a component.
function App() {
  return (
      //NOTE empty element wrapper needed because the return statement of a component can only return one fragment
      <>
        <h1>Component jsx code can go here</h1>
        <dl>
          <dt>JSX</dt>
          <dd>Is a syntax extension to JavaScript. JSX produces React “elements”</dd>
          <dd>
            Instead of artificially separating technologies by putting markup and logic in separate files,
            React separates concerns with loosely coupled units called “components” that contain both.
          </dd>
          
          <dt>Fragment</dt>
          <dd>Fragments let you group a list of children without adding extra nodes to the DOM.</dd>
        </dl>

        {/*This is a nested component*/}
        <WeatherScreen />
      </>
  );
  // return (
  //   <div className="App">
  //     <header className="App-header">
  //       <img src={logo} className="App-logo" alt="logo" />
  //       <p>
  //         Edit <code>src/App.js</code> and save to reload.
  //       </p>
  //       <a
  //         className="App-link"
  //         href="https://reactjs.org"
  //         target="_blank"
  //         rel="noopener noreferrer"
  //       >
  //         Learn React
  //       </a>
  //     </header>
  //   </div>
  // );
}

//NOTE this is used to make the "App" visible outside this file
export default App;
