import WeatherService from "../services/OpenWeatherMapService";
import Forecast from "../models/Forecast";
import { useState, useEffect } from "react";

function WeatherScreen() {

    useEffect(()=>{
        updateForecast();
    }, [])

    let [forecastData, setForecastData] = useState({});

    function updateForecast() {
        //NOTE city name should be taken as a ui input
        WeatherService.GetForecast('Sibiu')
            .then( result => {
                let forecast = new Forecast(result)
                console.log(forecast)
                setForecastData(forecast)
            })
            .then( cast => console.dir(cast))
            .catch( error => console.error(error) );            
    }

    return (
        <>
            <h3>Weather report</h3>
            <button onClick={updateForecast}>Update</button>
            <p>{forecastData.country} {forecastData.city}</p>
            <p>{forecastData.condition}</p>
            <p>Min: {forecastData.min}</p>
            <p>Max: {forecastData.max}</p>
            <p>Feels like: {forecastData.feel}</p>
            <p>Wind speed: {forecastData.windSpeed}</p>
            <p>Visibility: {forecastData.visibility}</p>
        </>
    );
}

//NOTE don't forget to export the function component so it can be imported in another file
export default WeatherScreen;
